import React, { useState, useEffect } from 'react';
import './App.css';

const ClosedPositions = () => {
  const [data, setData] = useState([]);
  const [lastFetchTime, setLastFetchTime] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [inputAddress, setInputAddress] = useState('');
  const [totalProfit, setTotalProfit] = useState(0);
  const [buttonClicked, setButtonClicked] = useState(false);

  useEffect(() => {
    if (buttonClicked) {
      async function fetchData() {
        setIsLoading(true);
        try {
          const response = await fetch(
            `https://api.vela.exchange/graph/closed_positions/42161/${encodeURIComponent(inputAddress)}/`
          );
          const jsonData = await response.json();

          if (lastFetchTime === null || jsonData[0]?.createdAt > lastFetchTime) {
            setData(jsonData);
            setLastFetchTime(jsonData[0]?.createdAt);
          }

          const calculatedTotalProfit = jsonData.reduce((total, activity) => {
            return total + parseFloat(activity.realisedPnl);
          }, 0);
          setTotalProfit(calculatedTotalProfit);
        } catch (error) {
          console.error('Error fetching data:', error);
        } finally {
          setIsLoading(false);
        }
      }

      fetchData();
      const interval = setInterval(fetchData, 30000);

      return () => {
        clearInterval(interval);
      };
    }
  }, [inputAddress, lastFetchTime, buttonClicked]);

  const handleButtonClick = () => {
    setButtonClicked(true);
  };

  const handleAddressChange = (event) => {
    setInputAddress(event.target.value);
  };

  return (
    <div className="App">
      <div className="header">
        <div className="logo-container">
          <img
            src={`${process.env.PUBLIC_URL}/logo.svg`}
            alt="React Logo"
            className={`logo ${isLoading ? 'spin' : ''}`}
          />
        </div>
        <h1>Closed Positions</h1>
        <input
          className="address-input"
          type="text"
          value={inputAddress}
          onChange={handleAddressChange}
          placeholder="Enter an address"
        />
        <button onClick={handleButtonClick}>Retrieve Data</button>
        <div className="total-profit"> Total Profit: {formatCurrency(totalProfit, 2)}</div>
      </div>
      {inputAddress === '' && !isLoading && (
        <p>Enter an address to retrieve closed positions.</p>
      )}
      {data.length === 0 && !isLoading && (
        <p>No closed positions available for the provided address.</p>
      )}
      <table className={`table ${isLoading ? 'hidden' : ''}`}>
                    <thead>
                        <tr>
                            <th>Position ID</th>
                            <th>Average Price</th>
                            <th>Collateral</th>
                            <th>Size</th>
                            <th>Created At</th>
                            <th>Closed At</th>
                            <th>Position Fee</th>
                            <th>Funding Fee</th>
                            <th>Borrow Fee</th>
                            <th>Token ID</th>
                            <th>Is Long</th>
                            <th>Mark Price</th>
                            <th>Order Status</th>
                            <th>Position Status</th>
                            <th>Position Type</th>
                            <th>Realised P/L</th>
                            <th>Total ROI</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((entry, index) => (
                            <tr key={index}>
                                <td>{entry.posId}</td>
                                <td>{formatCurrency(entry.averagePrice, 8)}</td>
                                <td>{formatCurrency(entry.collateral, 2)}</td>
                                <td>{formatCurrency(entry.size, 2)}</td>
                                <td>{new Date(entry.createdAt * 1000).toLocaleString()}</td>
                                <td>{new Date(entry.closedAt * 1000).toLocaleString()}</td>
                                <td>{formatCurrency(entry.positionFee, 2)}</td>
                                <td>{formatCurrency(entry.fundingFee, 2)}</td>
                                <td>{formatCurrency(entry.borrowFee, 2)}</td>
                                <td>{tokenLookup[parseInt(entry.tokenId)]}</td>
                                <td>{entry.isLong.toString()}</td>
                                <td>{formatCurrency(entry.markPrice, 8)}</td>
                                <td>{entry.orderStatus}</td>
                                <td>{toTitleCase(entry.positionStatus)}</td>
                                <td>{entry.positionType}</td>
                                <td>{formatCurrency(entry.realisedPnl, 2)}</td>
                                <td>{formatCurrency(entry.totalROI, 2)}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>)}

        </div>
    );
};

export default ClosedPositions;
